mapper.select:
 select f.*,t.name from eb_wallet_flow f
left join eb_team_user  u on u.uid = f.uid
left join eb_team t on t.id = u.tid
left join eb_user us on us.id = f.uid
where  1=1

    and   t.leader_id =#{teamId}


and   f.uId =#{uId}


    and   f.type =#{type}



    and   f.external_No =#{externalNo}



    and   f.action =#{action}



    and   us.nickname =#{nickname}




    and f.gmt_Created between #{startTime} and #{endTime}


order by f.id desc
