mapper.select:
select *,p.name as product_name,v.bar_code,u.nickname,u.account,t.name as team_name from eb_product_repertory_flow f left join eb_product p on f.product_id = p.id left join eb_product_attr_value v on f.product_id = v.product_id left join eb_user u on f.uid = u.id left join eb_team_user tu on f.uid = tu.uid left join eb_team t on tu.tid = t.id
and f.product_id=#{productId}
and f.uid=#{uid}
and u.nickname like CONCAT('%',#{nickname},'%')
order by f.id desc
