mapper.select:
        		select  count(1) from  tankactivate t
		left join tankequipment te on te.id =  t.equipmentId
		where te.storeId =  #{storeId}    and YEARWEEK(t.createdTime, 1) = YEARWEEK(CURDATE(), 1);

        	select  count(1) from  tankactivate t
		left join tankequipment te on te.id =  t.equipmentId
		where te.storeId =  #{storeId}
