mapper.select:
SELECT u.* FROM eb_user AS u


        and u.nickname like CONCAT('%',#{nikename},'%')


        and u.account like CONCAT('%',#{account},'%')


        and u.spread_uid=#{spreadid}


        and u.phone = #{phone, jdbcType=VARCHAR}


        and u.sex = #{sex}


        and ${tagIdSql}


        and u.register_type = #{registerType, jdbcType=VARCHAR}



            and u.pay_count = 0


            and u.pay_count >= #{payCount}



        and u.`is_promoter` = #{isPromoter}


        and u.`is_logoff` = #{isLogoff}




                and u.create_time between #{startTime} and #{endTime}
                and u.create_time = u.last_login_time


                and u.last_login_time between #{startTime} and #{endTime}


                and u.last_login_time not between #{startTime} and #{endTime}


                and u.last_login_time between #{startTime} and #{endTime}




ORDER BY u.id desc

SELECT u.* FROM eb_user AS u
RIGHT JOIN eb_user_merchant_collect um on u.id = um.uid
where
um.mer_id = #{merId}

    and u.nickname like CONCAT('%',#{nikename},'%')


    and u.phone = #{phone, jdbcType=VARCHAR}


    and u.sex = #{sex}


    and u.register_type = #{registerType, jdbcType=VARCHAR}


    and um.create_time between #{startTime} and #{endTime}

ORDER BY u.id desc

SELECT u.id,u.nickname, u.avatar, DATE_FORMAT(u.spread_time, '%Y-%m-%d %H:%i:%s') AS `time`, u.spread_count AS childCount, (SELECT count( * ) AS num FROM eb_user_brokerage_record AS br WHERE br.uid = #{uid} AND br.sub_uid = u.id AND br.STATUS = 3) AS orderCount, (SELECT IFNULL( sum( o.pay_price ), 0 ) AS price FROM eb_order AS o RIGHT JOIN eb_user_brokerage_record AS br ON br.link_no = o.order_no WHERE o.uid = u.id AND br.uid = #{uid} AND br.sub_uid = u.id AND o.STATUS > 1 AND br.STATUS = 3) AS amountCount FROM eb_user AS u where u.id in
#{userIdList}
and ( u.real_name like CONCAT('%',#{keywords},'%') or u.nickname like CONCAT('%',#{keywords},'%')
ORDER BY ${sortKey} ${sortValue}
